import React from 'react';
import { ArrowUpIcon, ArrowDownIcon } from '@heroicons/react/24/solid';
import Plot from 'react-plotly.js';
import { PortfolioMetric, PerformanceData, AgentSignalsData, Activity } from '../types';

const MetricCard: React.FC<PortfolioMetric> = ({ title, value, change, changeText }) => {
  const isPositive = change >= 0;
  
  return (
    <div className="bg-white rounded-lg shadow p-6">
      <h3 className="text-sm font-medium text-gray-500">{title}</h3>
      <div className="mt-2 flex items-baseline">
        <p className="text-2xl font-semibold text-gray-900">{value}</p>
        <span
          className={`ml-2 text-sm font-medium flex items-center ${
            isPositive ? 'text-success-500' : 'text-danger-500'
          }`}
        >
          {isPositive ? (
            <ArrowUpIcon className="h-4 w-4 mr-1" />
          ) : (
            <ArrowDownIcon className="h-4 w-4 mr-1" />
          )}
          {changeText}
        </span>
      </div>
    </div>
  );
};

const Dashboard: React.FC = () => {
  // Sample data - replace with real data from your backend
  const metrics: PortfolioMetric[] = [
    {
      title: 'Portfolio Value',
      value: '$1,234,567',
      change: 2.5,
      changeText: '2.5%'
    },
    {
      title: 'Daily P&L',
      value: '$12,345',
      change: 1.8,
      changeText: '+$12,345'
    },
    {
      title: 'Active Positions',
      value: '15',
      change: -2,
      changeText: '-2'
    },
    {
      title: 'Win Rate',
      value: '68%',
      change: 5,
      changeText: '+5%'
    }
  ];

  // Sample portfolio performance data
  const performanceData: PerformanceData = {
    x: Array.from({ length: 30 }, (_, i) => 
      new Date(Date.now() - (29 - i) * 24 * 60 * 60 * 1000).toISOString().split('T')[0]
    ),
    y: Array.from({ length: 30 }, (_, i) => 
      1000000 * (1 + Math.sin(i / 5) * 0.1 + i / 100)
    )
  };

  // Sample agent signals data
  const agentSignals: AgentSignalsData = {
    agents: ['Buffet', 'Ackman', 'Technical', 'Sentiment', 'Fundamental', 'Research'],
    confidence: [0.85, 0.72, 0.45, 0.65, 0.78, 0.62],
    signals: ['BUY', 'BUY', 'HOLD', 'SELL', 'BUY', 'HOLD']
  };

  // Sample activities
  const activities: Activity[] = [
    {
      action: 'BUY',
      symbol: 'AAPL',
      quantity: 100,
      price: 185.92,
      agent: 'Buffet Agent',
      time: '2 minutes ago'
    },
    {
      action: 'SELL',
      symbol: 'MSFT',
      quantity: 50,
      price: 372.65,
      agent: 'Technical Agent',
      time: '15 minutes ago'
    },
    {
      action: 'BUY',
      symbol: 'GOOGL',
      quantity: 25,
      price: 142.71,
      agent: 'Research Agent',
      time: '1 hour ago'
    }
  ];

  return (
    <div className="space-y-6 animate-fade-in">
      {/* Metrics Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {metrics.map((metric) => (
          <MetricCard key={metric.title} {...metric} />
        ))}
      </div>

      {/* Charts Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Portfolio Performance Chart */}
        <div className="bg-white rounded-lg shadow p-6">
          <h3 className="text-lg font-medium text-gray-900 mb-4">
            Portfolio Performance
          </h3>
          <Plot
            data={[
              {
                x: performanceData.x,
                y: performanceData.y,
                type: 'scatter',
                mode: 'lines',
                line: { color: '#0ea5e9' }
              }
            ]}
            layout={{
              height: 300,
              margin: { t: 10, r: 10, b: 40, l: 60 },
              xaxis: { showgrid: false },
              yaxis: { 
                tickformat: '$,.0f',
                showgrid: true,
                gridcolor: '#f1f5f9'
              },
              paper_bgcolor: 'rgba(0,0,0,0)',
              plot_bgcolor: 'rgba(0,0,0,0)'
            }}
            config={{ responsive: true, displayModeBar: false }}
          />
        </div>

        {/* Agent Signals Chart */}
        <div className="bg-white rounded-lg shadow p-6">
          <h3 className="text-lg font-medium text-gray-900 mb-4">
            Agent Signals
          </h3>
          <Plot
            data={[
              {
                type: 'bar',
                x: agentSignals.confidence,
                y: agentSignals.agents,
                orientation: 'h',
                marker: {
                  color: agentSignals.signals.map(signal => 
                    signal === 'BUY' ? '#22c55e' : 
                    signal === 'SELL' ? '#ef4444' : '#94a3b8'
                  )
                },
                text: agentSignals.signals,
                textposition: 'auto',
              }
            ]}
            layout={{
              height: 300,
              margin: { t: 10, r: 10, b: 40, l: 120 },
              xaxis: { 
                title: 'Confidence',
                showgrid: true,
                gridcolor: '#f1f5f9',
                range: [0, 1]
              },
              yaxis: { showgrid: false },
              paper_bgcolor: 'rgba(0,0,0,0)',
              plot_bgcolor: 'rgba(0,0,0,0)'
            }}
            config={{ responsive: true, displayModeBar: false }}
          />
        </div>
      </div>

      {/* Recent Activity */}
      <div className="bg-white rounded-lg shadow overflow-hidden">
        <div className="px-6 py-4 border-b border-gray-200">
          <h3 className="text-lg font-medium text-gray-900">Recent Activity</h3>
        </div>
        <div className="divide-y divide-gray-200">
          {activities.map((activity, i) => (
            <div key={i} className="px-6 py-4">
              <div className="flex items-center justify-between">
                <div className="flex items-center">
                  <span
                    className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                      activity.action === 'BUY'
                        ? 'bg-success-50 text-success-700'
                        : 'bg-danger-50 text-danger-700'
                    }`}
                  >
                    {activity.action}
                  </span>
                  <span className="ml-2 text-sm text-gray-900">
                    {activity.quantity} {activity.symbol} @ ${activity.price}
                  </span>
                </div>
                <div className="flex items-center text-sm text-gray-500">
                  <span className="mr-2">{activity.agent}</span>
                  <span>{activity.time}</span>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
